import React, { Component } from 'react';
import '../App.css';
class Counter extends Component {
    addCount() {
        alert("It Worked!");
      }
    render() { 
        return ( 
            <div className="container">
            <div className="counterHeader">
                <h1 className="display-4 text-dark text-center">React Counter</h1>
                </div>
              <div className="counterContent">
                <div className="counterBody">
                <h1 className="display-4 counterNumber">{counterNumber}</h1>
                <div className="counterButtons">
                <button className="btn btn-primary" id="addCount" onClick={this.addCount}>Add Count</button><br/><br/>
                <button className="btn btn-success" id="lowerCount">Lower Count</button>
                </div>
                </div>
              </div>
            </div>
         );
    }
}
 
export default Counter;
