import React, { Component } from 'react';
import '../App.css';
class Counter extends Component {
    constructor(props) {
        super(props);
        this.state = {
          count: 0
        };
    }
    
    render() { 
        
        
        return ( 
            <div className="container">
            <div className="counterHeader">
                <h1 className="display-4 text-dark text-center">React Counter</h1>
                </div>
              <div className="counterContent">
                <div className="counterBody">
                <h1 className="display-4 counterNumber">{this.state.count}</h1>
                <div className="counterButtons">
                <button className="btn btn-primary" id="addCount" onClick={this.addCount}>Add Count</button><br/><br/>
                <button className="btn btn-success" id="lowerCount" onClick={this.lowerCount}>Lower Count</button><br/><br/>
                <button className="btn btn-danger" id="resetCount" onClick={this.resetCount}>Reset</button>
                </div>
                </div>
              </div>
            </div>
         );
    }
}
 
export default Counter;
